// n8n Function Item Node: Clean Academic Paper Text
// Input: $json.text (raw text from PDF)
// Output: { cleaned_text: "..." }

const rawText = $input.first().json.text || '';

if (!rawText || typeof rawText !== 'string') {
  return { json: { cleaned_text: '', warning: 'Input text is empty or invalid' } };
}

let text = rawText;

// 5. Remove reference section and everything after
const refPatterns = [
  /^references$/im,
  /^bibliography$/im,
  /^å‚è€ƒæ–‡çŒ®$/im,
  /^acknowledgement/i,
  /^acknowledgments/i,
  /^è‡´è°¢$/i,
  /^appendix/i,
  /^supplementary material/i,
  /^ethics statement/i
];

for (const pattern of refPatterns) {
  const match = text.match(pattern);
  if (match) {
    const idx = match.index;
    text = text.substring(0, idx).trim();
    break;
  }
}

return {
  json: {
    cleaned_text: text,
    original_length: rawText.length,
    cleaned_length: text.length,
    reduction_ratio: ((1 - text.length / rawText.length) * 100).toFixed(1) + '%'
  }
};
